add_action( 'parse_request', 'free_freeuk_map_plugin_wp_request' );

function free_freeuk_map_plugin_wp_request( $wp ) {
    if( isset($_GET['freefreeukmap_js_data']) ) {
        header( 'Content-Type: application/javascript' );
       ?>
    var
        nameColor		= "<?php echo get_option('freefreeukhtml5map_nameColor')?>",
        nameFontSize		= "<?php echo get_option('freefreeukhtml5map_nameFontSize')?>",
        map_data = <?php echo get_option('freefreeukhtml5map_map_data')?>;
        <?php
        exit;
    }

    if(isset($_GET['freefreeukmap_get_state_info'])) {
        $stateId = (int) $_GET['freefreeukmap_get_state_info'];
        echo nl2br(get_option('freefreeukhtml5map_state_info_'.$stateId));
        exit;
    }
}

register_activation_hook( __FILE__, 'free_freeuk_map_plugin_activation' );

function free_freeuk_map_plugin_activation() {
    $initialStatesPath = dirname(__FILE__).'/static/settings_tpl.json';
    add_option('freefreeukhtml5map_map_data', file_get_contents($initialStatesPath));
    add_option('freefreeukhtml5map_nameColor', "#000000");
    add_option('freefreeukhtml5map_nameFontSize', "12px");

    for($i = 1; $i <= 14; $i++) {
        add_option('freefreeukhtml5map_state_info_'.$i, '');
    }
}

